library(stringr)
library(glue)
#Definir Enzima de Restricci√≥n
enzima_restriccion <- function(seq, sitio){
  cortes <- unlist(gregexpr(sitio, seq))
  fragmentos <- c(diff(cortes), nchar(seq)-cortes[length(cortes)])
  return(fragmentos)
}

#Definir la secuencia de ADN y sitio de corte
ADN <- ""
hinfi <- "GAGTC"

#Obtener los fragmentos de restriccion
fragments <- enzima_restriccion(ADN, hinfi)


#Dibujar las bandas del gel electroforetico de Agarosa del ADN fragmentado
ry <- range(fragments) * c(0.5, 1.5)
par (bg = "black")
plot(1, type="n", ylim = ry, log = "y", xlim = c(0,2))


for(i in fragments){
  segments(0.5, i, 1.5, i, lwd=5, col = "grey")
}

axis(2, at = fragments, labels = fragments, pos = 0.4, las = 1, col = "white", col.axis = "white")
